var module = require('./src/module_body');

var actionWithFile = function(action, fileName){
	switch(action){
		case 'copy':
			return module.copy(fileName);
			break;
		case 'del':
			return module.del(fileName);
			break;
		case 'index':
			return module.getAllFileNames(fileName);
		default:
			return 'Unrecognised command';
			break;
	}
}
exports.actionWithFile = actionWithFile;
